package com.hblong.btday13.fragment;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.fragment.app.FragmentManager;
import androidx.fragment.app.FragmentPagerAdapter;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import androidx.viewpager.widget.PagerAdapter;

import com.hblong.btday13.R;
import com.hblong.btday13.adapter.PageAdapter;
import com.hblong.btday13.model.Page;

import java.util.ArrayList;
import java.util.List;

public class MusicFragment extends Fragment {
    private RecyclerView rscv;
    private PageAdapter pagerAdapter;
    private List<Page> pages;

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        View view = inflater.inflate(R.layout.fragment_music, container, false);

        rscv = (RecyclerView) view.findViewById(R.id.rscv);

        pages = new ArrayList<>();
        pages.add(new Page("Zing Mp3", "https://top10tphcm.com/wp-content/uploads/2018/12/Zing-MP3.jpg"));
        pages.add(new Page("Nhaccuatui", "data:image/jpeg;base64,"));
        pages.add(new Page("Chiasenhac", "https://top10tphcm.com/wp-content/uploads/2018/12/chiasenhac.jpg"));
        pages.add(new Page("Nhac.vn", "data:image/jpeg;base64,"));
        pages.add(new Page("Nhacdj.vn", "https://top10tphcm.com/wp-content/uploads/2018/12/nhacdj.jpg"));
        pages.add(new Page("Mix166", "data:image/png;base64,"));
        pages.add(new Page("Keeng.vn", "data:image/jpeg;base64,"));
        pages.add(new Page("Imuzik", "https://top10tphcm.com/wp-content/uploads/2018/12/Imusik.jpg"));
        pages.add(new Page("Youtube Music", "https://top10tphcm.com/wp-content/uploads/2018/12/Youtube-Music.jpg"));
        pages.add(new Page("Soundclound", "https://top10tphcm.com/wp-content/uploads/2018/12/SoundCloud.jpg"));

        pagerAdapter = new PageAdapter(getContext(), pages);

        rscv.setAdapter(pagerAdapter);

        rscv.setLayoutManager(new LinearLayoutManager(getContext()));

        return view;
    }
}
